const Firebase = require('firebase');

let firebaseURL;

const set = (path, data) => {
    const ref = new Firebase(`${firebaseURL}/${path}`);
    ref.set(data);
};

const push = (path, data) => {
    const ref = new Firebase(`${firebaseURL}/${path}`);
    const newId = ref.push(data).key();

    return newId;
};

const get = async (path) => {
    const ref = new Firebase(`${firebaseURL}/${path}`);
    const dataSnapshot = await ref.once('value');
    return dataSnapshot.val();
};

const createUser = async (email, password) => {
    const ref = new Firebase(`${firebaseURL}`);

    const userData = await ref.createUser({
        email,
        password
    });

    return userData;
};

const logInUser = async (email, password) => {
    const ref = new Firebase(`${firebaseURL}`);

    const authData = await ref.authWithPassword({
        email,
        password
    });

    return authData;
};

const logOutUser = () => {
    const ref = new Firebase(`${firebaseURL}`);
    ref.unauth();
};

const isUserLoggedIn = () => {
    const ref = new Firebase(`${firebaseURL}`);
    return ref.getAuth();
};

const setDBPath = (url) => {
    firebaseURL = url;
};

export const FirebaseService = {
    setDBPath,
    set,
    push,
    get,
    createUser,
    logInUser,
    logOutUser,
    isUserLoggedIn
};
